mapper.insert:
IF NOT EXISTS(SELECT 1 FROM GoldCard_Info  WHERE meterNo = #{meterNo} )
BEGIN
  insert into GoldCard_Info (serialNo
,meterState
,setUpTime
,beseNum
,readingNum
,userName
,address
,userType
,deviceId
,valveState
,sumUseGas
,sumUseMoney
,priceCode
,sumRechargeAomunt
,lastReadingTime
,balance
,meterNo
,price
,lastRechargeAmount
,lastRechargeDate)values ( #{serialNo},#{meterState}
,#{setUpTime}
,#{beseNum}
,#{readingNum}
,#{userName}
,#{address}
,#{userType}
,#{deviceId}
,#{valveState}
,#{sumUseGas}
,#{sumUseMoney}
,#{priceCode}
,#{sumRechargeAomunt}
,#{lastReadingTime}
,#{balance}
,#{meterNo}
,#{price}
,#{lastRechargeAmount}
,#{lastRechargeDate});
END
ELSE
BEGIN
  UPDATE GoldCard_Info set serialNo=#{serialNo}
,meterState=#{meterState}
,setUpTime=#{setUpTime}
,beseNum=#{beseNum}
,readingNum=#{readingNum}
,userName=#{userName}
,address=#{address}
,userType=#{userType}
,deviceId=#{deviceId}
,valveState=#{valveState}
,sumUseGas=#{sumUseGas}
,sumUseMoney=#{sumUseMoney}
,priceCode=#{priceCode}
,sumRechargeAomunt=#{sumRechargeAomunt}
,lastReadingTime=#{lastReadingTime}
,balance=#{balance}
,meterNo=#{meterNo}
,price=#{price}
,lastRechargeAmount=#{lastRechargeAmount}
,lastRechargeDate=#{lastRechargeDate}  where meterNo = #{meterNo};
END
